/* GT3 rebrand colors imported by Faramarz Hashemi - 2024-06-13 */
/* GTreasury Color Tokens - Updated with comprehensive palette */
:root {
  /* GTreasury Primary Colors */
  --night-blue: #012030;
  --sea-blue: #115d7e;
  --peppermint-green: #45c4b0;
  --monsoon-green: #9aeba3;
  --white: #ffffff;

  /* GTreasury Secondary Colors */
  --lime-green: #baeb4f;
  --sage-green: #d9fcba;
  --pale-yellow: #fffad6;

  /* GSmart Colors */
  --gsmart-primary: #e33277;
  --gsmart-secondary: #f4c548;
  --gsmart-gradient-start: #e33277;
  --gsmart-gradient-end: #f4c548;

  /* GSmart Gradient Stops */
  --gsmart-gradient-31: #e33277; /* 31% stop */
  --gsmart-gradient-100: #f4c548; /* 100% stop */

  /* GSmart Tints and Shades */
  --gsmart-primary-dark: #c12a66;
  --gsmart-primary-light: #e85c95;
  --gsmart-secondary-dark: #e6b73a;
  --gsmart-secondary-light: #f7d066;

  /* Night Blue Tints */
  --night-blue-100: #012030;
  --night-blue-90: #1a3645;
  --night-blue-80: #344d59;
  --night-blue-70: #4d636e;
  --night-blue-60: #677983;
  --night-blue-50: #809098;
  --night-blue-40: #99a6ac;
  --night-blue-30: #b3bcc1;
  --night-blue-20: #ccd2d6;
  --night-blue-10: #e6e9ea;

  /* Sea Blue Tints */
  --sea-blue-130: #0c4158;
  --sea-blue-120: #0e4a65;
  --sea-blue-110: #0f5471;
  --sea-blue-100: #115d7e;
  --sea-blue-90: #296d8b;
  --sea-blue-80: #417d98;
  --sea-blue-70: #588ea5;
  --sea-blue-60: #709eb2;
  --sea-blue-50: #88aebf;
  --sea-blue-40: #a0bec8;
  --sea-blue-30: #b8ced8;
  --sea-blue-20: #cfdfe5;
  --sea-blue-10: #e7eff2;

  /* Peppermint Green Tints */
  --peppermint-green-150: #236258;
  --peppermint-green-140: #29766a;
  --peppermint-green-130: #30897b;
  --peppermint-green-120: #379b8d;
  --peppermint-green-110: #3eb09e;
  --peppermint-green-100: #45c4b0;
  --peppermint-green-90: #58cab8;
  --peppermint-green-80: #6ad0c0;
  --peppermint-green-70: #7dd6c8;
  --peppermint-green-60: #8fdcd0;
  --peppermint-green-50: #a2e2d8;
  --peppermint-green-40: #b5e7df;
  --peppermint-green-30: #c7ede7;
  --peppermint-green-20: #daf3ef;
  --peppermint-green-10: #ecf9f7;

  /* Monsoon Green Tints */
  --monsoon-green-150: #4d7652;
  --monsoon-green-140: #5c8d62;
  --monsoon-green-130: #6ca572;
  --monsoon-green-120: #7bbc82;
  --monsoon-green-110: #8bd493;
  --monsoon-green-100: #9aeba3;
  --monsoon-green-90: #a4edac;
  --monsoon-green-80: #aeefb5;
  --monsoon-green-70: #b8f1bf;
  --monsoon-green-60: #c2f3c8;
  --monsoon-green-50: #cdf5d1;
  --monsoon-green-40: #d7f7da;
  --monsoon-green-30: #e1f9e3;
  --monsoon-green-20: #ebfbed;
  --monsoon-green-10: #f5fdf6;

  /* Theme Colors - Mapped to GTreasury Primary Colors for AA Compliance */
  --background: var(--white); /* #FFFFFF - Pure white background */
  --foreground: var(--night-blue); /* #012030 - Night Blue for high contrast text (16.3:1) */

  --card: var(--white); /* #FFFFFF - White card backgrounds */
  --card-foreground: var(--night-blue); /* #012030 - Night Blue text on cards (16.3:1) */

  --popover: var(--white); /* #FFFFFF - White popover backgrounds */
  --popover-foreground: var(--night-blue); /* #012030 - Night Blue text in popovers (16.3:1) */

  --primary: var(--sea-blue); /* #115D7E - Sea Blue for primary actions (5.8:1 on white) */
  --primary-foreground: var(--white); /* #FFFFFF - White text on Sea Blue (5.8:1) */

  --secondary: var(--peppermint-green); /* #45C4B0 - Peppermint Green for secondary actions */
  --secondary-foreground: var(--night-blue); /* #012030 - Night Blue text on Peppermint Green */

  --muted: var(--night-blue-10); /* #E6E9EA - Very light Night Blue for muted backgrounds */
  --muted-foreground: var(--night-blue-70); /* #4D636E - Medium Night Blue for muted text */

  --accent: var(--peppermint-green-20); /* #DAF3EF - Light Peppermint Green for accents */
  --accent-foreground: var(--night-blue); /* #012030 - Night Blue text on light accents */

  --destructive: #dc2626; /* Red for destructive actions - keeping standard for accessibility */
  --destructive-foreground: var(--white); /* #FFFFFF - White text on red */

  --border: var(--night-blue-20); /* #CCD2D6 - Light Night Blue for borders */
  --input: var(--night-blue-20); /* #CCD2D6 - Light Night Blue for input borders */
  --ring: var(--sea-blue); /* #115D7E - Sea Blue for focus rings */

  /* Chart colors updated for proper GTreasury color hierarchy */
  /* PRIMARY COLORS FIRST */
  --chart-1: var(--night-blue); /* #012030 - PRIMARY Night Blue (16.3:1 contrast) */
  --chart-2: var(--sea-blue); /* #115D7E - PRIMARY Sea Blue (5.8:1 contrast) */
  --chart-3: var(--peppermint-green); /* #45C4B0 - PRIMARY Peppermint Green */
  --chart-4: var(--monsoon-green); /* #9AEBA3 - PRIMARY Monsoon Green */
  /* SECONDARY COLORS NEXT */
  --chart-5: var(--lime-green); /* #BAEB4F - SECONDARY Lime Green */
  /* ONLY USE SHADES WHEN PRIMARY/SECONDARY ARE EXHAUSTED */
  --chart-6: var(--sea-blue-80); /* #417D98 - Sea Blue shade for additional data */
  --chart-7: var(--peppermint-green-130); /* #30897B - Peppermint Green shade */
  --chart-8: var(--monsoon-green-130); /* #6CA572 - Monsoon Green shade */
  --chart-9: var(--night-blue-70); /* #4D636E - Night Blue shade */
  --chart-10: var(--sage-green); /* #D9FCBA - SECONDARY Sage Green */

  --sidebar: oklch(0.932 0.0035 219.5341);
  --sidebar-foreground: oklch(0.2302 0.0476 235.2985);
  --sidebar-primary: oklch(0.4839 0.0927 232.2901);
  --sidebar-primary-foreground: oklch(1.0 0 0);
  --sidebar-accent: oklch(0.9448 0.0267 185.163);
  --sidebar-accent-foreground: oklch(0.3791 0.1378 265.5222);
  --sidebar-border: oklch(0.947 0.0095 222.059);
  --sidebar-ring: oklch(0.8696 0.1268 147.0805);

  --font-sans: Inter, sans-serif;
  --font-serif: ui-serif, Georgia, Cambria, "Times New Roman", Times, serif;
  --font-mono: JetBrains Mono, monospace;
  --radius: 0.375rem;
  --shadow-2xs: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.04);
  --shadow-xs: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.04);
  --shadow-sm: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.09), 0px 1px
    2px -1px hsl(200.4255 95.9184% 9.6078% / 0.09);
  --shadow: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.09), 0px 1px 2px
    -1px hsl(200.4255 95.9184% 9.6078% / 0.09);
  --shadow-md: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.09), 0px 2px
    4px -1px hsl(200.4255 95.9184% 9.6078% / 0.09);
  --shadow-lg: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.09), 0px 4px
    6px -1px hsl(200.4255 95.9184% 9.6078% / 0.09);
  --shadow-xl: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.09), 0px 8px
    10px -1px hsl(200.4255 95.9184% 9.6078% / 0.09);
  --shadow-2xl: 0px 2px 3px 0px hsl(200.4255 95.9184% 9.6078% / 0.22);
}

