  --font-sans: var(--font-sans);
  --font-mono: var(--font-mono);
  --font-serif: var(--font-serif);

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);

  --shadow-2xs: var(--shadow-2xs);
  --shadow-xs: var(--shadow-xs);
  --shadow-sm: var(--shadow-sm);
  --shadow: var(--shadow);
  --shadow-md: var(--shadow-md);
  --shadow-lg: var(--shadow-lg);
  --shadow-xl: var(--shadow-xl);
  --shadow-2xl: var(--shadow-2xl);
}

/* GSmart Gradient Utilities */
.gsmart-gradient {
  background: linear-gradient(
    135deg,
    var(--gsmart-gradient-start) 31%,
    var(--gsmart-gradient-end) 100%
  );
}

.gsmart-gradient-horizontal {
  background: linear-gradient(
    90deg,
    var(--gsmart-gradient-start) 31%,
    var(--gsmart-gradient-end) 100%
  );
}

.gsmart-gradient-vertical {
  background: linear-gradient(
    180deg,
    var(--gsmart-gradient-start) 31%,
    var(--gsmart-gradient-end) 100%
  );
}

.gsmart-gradient-radial {
  background: radial-gradient(
    circle,
    var(--gsmart-gradient-start) 31%,
    var(--gsmart-gradient-end) 100%
  );
}

/* GSmart Text Gradients */
.gsmart-text-gradient {
  background: linear-gradient(
    135deg,
    var(--gsmart-gradient-start) 31%,
    var(--gsmart-gradient-end) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* GSmart Border Gradients */
.gsmart-border-gradient {
  border: 2px solid transparent;
  background: linear-gradient(white, white) padding-box,
    linear-gradient(
      135deg,
      var(--gsmart-gradient-start) 31%,
      var(--gsmart-gradient-end) 100%
    )
    border-box;
}

