@tailwind base;
@tailwind components;
@tailwind utilities;

/* GT3 rebrand colors imported by Faramarz Hashemi - 2024-06-13 */
@layer base {
  :root {
    --background: oklch(1.0000 0 0);
    --foreground: oklch(0.2302 0.0476 235.2985);

    --card: oklch(1.0000 0 0);
    --card-foreground: oklch(0.2302 0.0476 235.2985);

    --popover: oklch(1.0000 0 0);
    --popover-foreground: oklch(0.2302 0.0476 235.2985);

    --primary: oklch(0.4513 0.0871 233.2261);
    --primary-foreground: oklch(1.0000 0 0);

    --secondary: oklch(0.8696 0.1268 147.0805);
    --secondary-foreground: oklch(0.2302 0.0476 235.2985);

    --muted: oklch(0.9470 0.0095 222.0590);
    --muted-foreground: oklch(0.4049 0.0365 229.4073);

    --accent: oklch(0.9448 0.0267 185.1630);
    --accent-foreground: oklch(0.2302 0.0476 235.2985);

    --destructive: oklch(0.6368 0.2078 25.3313);
    --destructive-foreground: oklch(1.0000 0 0);

    --border: oklch(0.7474 0.0468 231.9162);
    --input: oklch(0.7474 0.0468 231.9162);
    --ring: oklch(0.4839 0.0927 232.2901);

    --chart-1: oklch(0.4839 0.0927 232.2901);
    --chart-2: oklch(0.7455 0.1144 180.5804);
    --chart-3: oklch(0.8696 0.1268 147.0805);
    --chart-4: oklch(0.8769 0.1874 125.1799);
    --chart-5: oklch(0.9499 0.0934 130.7273);

    --sidebar: oklch(0.9320 0.0035 219.5341);
    --sidebar-foreground: oklch(0.2302 0.0476 235.2985);
    --sidebar-primary: oklch(0.4839 0.0927 232.2901);
    --sidebar-primary-foreground: oklch(1.0000 0 0);
    --sidebar-accent: oklch(0.9448 0.0267 185.1630);
    --sidebar-accent-foreground: oklch(0.3791 0.1378 265.5222);
    --sidebar-border: oklch(0.9470 0.0095 222.0590);
    --sidebar-ring: oklch(0.8696 0.1268 147.0805);

    --radius: 0.375rem;
  }

  .dark {
    --background: oklch(0.2046 0 0);
    --foreground: oklch(0.9219 0 0);

    --card: oklch(0.2686 0 0);
    --card-foreground: oklch(0.9219 0 0);

    --popover: oklch(0.2686 0 0);
    --popover-foreground: oklch(0.9219 0 0);

    --primary: oklch(0.4839 0.0927 232.2901);
    --primary-foreground: oklch(1.0000 0 0);

    --secondary: oklch(0.2686 0 0);
    --secondary-foreground: oklch(0.9219 0 0);

    --muted: oklch(0.2686 0 0);
    --muted-foreground: oklch(0.7155 0 0);

    --accent: oklch(0.3791 0.1378 265.5222);
    --accent-foreground: oklch(0.8823 0.0571 254.1284);

    --destructive: oklch(0.6368 0.2078 25.3313);
    --destructive-foreground: oklch(1.0000 0 0);

    --border: oklch(0.3715 0 0);
    --input: oklch(0.3715 0 0);
    --ring: oklch(0.6231 0.1880 259.8145);

    --chart-1: oklch(0.7137 0.1434 254.6240);
    --chart-2: oklch(0.6231 0.1880 259.8145);
    --chart-3: oklch(0.5461 0.2152 262.8809);
    --chart-4: oklch(0.4882 0.2172 264.3763);
    --chart-5: oklch(0.4244 0.1809 265.6377);

    --sidebar: oklch(0.2302 0.0476 235.2985);
    --sidebar-foreground: oklch(0.9219 0 0);
    --sidebar-primary: oklch(0.4839 0.0927 232.2901);
    --sidebar-primary-foreground: oklch(1.0000 0 0);
    --sidebar-accent: oklch(0.8696 0.1268 147.0805);
    --sidebar-accent-foreground: oklch(0.2302 0.0476 235.2985);
    --sidebar-border: oklch(0.3715 0 0);
    --sidebar-ring: oklch(0.2302 0.0476 235.2985);
  }

  * {
    border-color: hsl(var(--border));
  }

  body {
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }
}

import type { Config } from "tailwindcss";

const config: Config = {
  content: [
    "./pages/**/*.{ts,tsx,js,jsx,mdx}",
    "./components/**/*.{ts,tsx,js,jsx,mdx}",
    "./app/**/*.{ts,tsx,js,jsx,mdx}",
    "*.{ts,tsx,js,jsx,mdx}",
  ],
  theme: {
    extend: {
      colors: {
        background: "hsl(var(--background))",
        foreground: "hsl(var(--foreground))",
        card: "hsl(var(--card))",
        "card-foreground": "hsl(var(--card-foreground))",
        popover: "hsl(var(--popover))",
        "popover-foreground": "hsl(var(--popover-foreground))",
        primary: "hsl(var(--primary))",
        "primary-foreground": "hsl(var(--primary-foreground))",
        secondary: "hsl(var(--secondary))",
        "secondary-foreground": "hsl(var(--secondary-foreground))",
        muted: "hsl(var(--muted))",
        "muted-foreground": "hsl(var(--muted-foreground))",
        accent: "hsl(var(--accent))",
        "accent-foreground": "hsl(var(--accent-foreground))",
        destructive: "hsl(var(--destructive))",
        "destructive-foreground": "hsl(var(--destructive-foreground))",
        border: "hsl(var(--border))",
        input: "hsl(var(--input))",
        ring: "hsl(var(--ring))",
        chart: {
          1: "hsl(var(--chart-1))",
          2: "hsl(var(--chart-2))",
          3: "hsl(var(--chart-3))",
          4: "hsl(var(--chart-4))",
          5: "hsl(var(--chart-5))",
        },
        sidebar: {
          DEFAULT: "hsl(var(--sidebar))",
          foreground: "hsl(var(--sidebar-foreground))",
          primary: "hsl(var(--sidebar-primary))",
          "primary-foreground": "hsl(var(--sidebar-primary-foreground))",
          accent: "hsl(var(--sidebar-accent))",
          "accent-foreground": "hsl(var(--sidebar-accent-foreground))",
          border: "hsl(var(--sidebar-border))",
          ring: "hsl(var(--sidebar-ring))",
        },
      },
      borderRadius: {
        lg: "var(--radius)",
        md: "calc(var(--radius) - 2px)",
        sm: "calc(var(--radius) - 4px)",
      },
      fontFamily: {
        sans: ["Inter", "sans-serif"],
        serif: ["ui-serif", "Georgia", "Cambria", "Times New Roman", "Times", "serif"],
        mono: ["JetBrains Mono", "monospace"],
      },
    },
  },
  plugins: [require("tailwindcss-animate")],
};
export default config;

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}
